sql.execute('DELETE FROM qa_jdbc WHERE WORKINGNAME = ?', workingName)

sql.execute('INSERT INTO qa_jdbc (WORKINGNAME, CLOB_DATA) VALUES (?, ?)', [workingName, clobText] )

