  if (i < 10){
    num = '0'+i
  } else {
    num = i
  }
  sql.execute('INSERT INTO qa_jdbc (workingname, blob_data, clob_data) VALUES (?, ?, ?)', [workingName+num, null, 'num=' + num] )
